import React from 'react';
import { connect } from 'react-redux';

import { deleteChat } from '../../actions/index';

import Comment from './Comment';

class Message extends React.Component {

    renderAdminButton = () => {
        const {user, userId} = this.props;
        if (user && userId) {
            if (this.props.user.userId == this.props.userId) {
                return (
                    <div className="ui icon top right pointing dropdown button">
                        <i onClick={() => console.log('clicked')} className="settings icon"/>
                        <div className="menu">
                            <div className="header">Admin settings</div>
                        </div>
                    </div>
                )
            }
        }
    };
    render() {
        return (
            <div className="ui three column grid">
                <div className="row">
                    <div className="column">
                        <div className="ui segment">
                            <Comment author={this.props.userName} message={this.props.message} img={this.props.userPic}/>
                        </div>
                    </div>
                    <div className="right floated column">
                        {this.renderAdminButton()}
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        user: state.auth
    }
};

export default connect(mapStateToProps, {deleteChat})(Message)
