import {SIGN_IN, SIGN_OUT} from '../actions/types';

const INITIAL_STATE = {
    isSignedIn: null,
    userName: null,
    userId: null,
    userPic: null
};


export default (state = INITIAL_STATE, {type, payload}) => {
    switch (type) {
        case SIGN_IN:
            return {...state, isSignedIn: true, userId: payload.getId(), userName: payload.getName()};
        case SIGN_OUT:
            return {...state, isSignedIn: false, userId: null, userName: null};
        default:
            return state;
    }
}
